create database n;

use n;

#管理员表
create table manager
(
	id				int				auto_increment			primary key,
	userName		varchar(100)	not null,
	password		varchar(100)	not null,
	userType		varchar(20)		not null,
	remark			varchar(200)		null,
	addnum			int				default 0
);
insert into manager(userName,password,userType)values('admin','123','系统管理员');

#新闻分类表
create table newsTypes
(
	typeId			int				auto_increment			primary key,
	typeName		varchar(40)		not null,
	articleNums		int				default 0
);
insert into newsTypes(typeName,articleNums)values('国内新闻',7);
insert into newsTypes(typeName,articleNums)values('体育新闻',6);
insert into newsTypes(typeName,articleNums)values('国际新闻',3);
insert into newsTypes(typeName,articleNums)values('科技新闻',9);

#新闻表
create table newsArticles
(
	articleId		int				auto_increment			primary key,
	content			text			not null,
	title			varchar(500)	not null,
	typeId			int				not null,
	foreign key (typeId) references newsTypes(typeId),
	userName		varchar(100)	not null,
	writer			varchar(100)		null,
	source			varchar(400)		null,
	hints			int				default 0,
	dateandtime		timestamp		default current_timestamp,
	checkup			int				default 0,
	imagepath		varchar(100)		null
);
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('人民网济南8月9日电 记者宋光茂报道：继“7.18”特大暴雨之后，又一场雷暴雨在今晚九点以后袭击济南。 　　<BR><BR>持续了一整天的特别闷热天气，终于酿就了入夜后的倾盆雷暴雨。在市区马路上行车，雨刷开到最快档，也仍来不及刮开车前的雨幕，让人几乎看不清道路。只有那一条条火龙般的闪电，频频划破夜空，照亮地面的积水，就像带着极强闪光灯的照相机，在空中不断按动快门，给大地拍照。 　　<BR><BR>受强闪电影响，记者所到的有些路段的路灯，时常整体熄灭，给司机突增某种可怕之感。显然，这些路段的路灯安装了某种感应装置，以防雷电损坏。 　　<BR><BR>可能市民接受了“7.18”的教训，再加上暴雨来袭时间也过了下班高峰，济南路面上的车辆较少，没有出现拥堵和车辆滞留问题。毕竟今晚的雨量比“7.18”小得多，估计今晚应不会造成什么损失。 　　<BR><BR>截至记者发稿时，雨仍在下，雷吼声不断。 ','山东济南再次遭受雷暴雨袭击','1','ccc ',' ',' ','0','0','newspicture/U1565P1T247D4646F4939DT20070815121942.jpg');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('昨日上午9时，邯郸市中级人民法院。法槌响处，中院院长、邯郸金库盗案审判长许广为庄严宣布：“任晓峰、马向景判处死刑……”至此，这起建国以来涉案金额最大的银行金库监守自盗案有了初步结果。 　　<BR>自案发至今四个月间，这起震惊国人的大案始终吸引着国人的关注和讨论。而关于任晓峰等人的行为动机、5100万元巨款买彩票血本无归的内幕，以及对犯罪嫌疑人应以贪污还是挪用资金来定罪量刑、任马二人是否应判死刑等话题的争论，也随着审判过程的层层深入，由模糊变得清晰起来。 　　<BR>这桩看起来充满荒唐色彩的案件，不但演绎了几个原本正常轨道上的不同人生故事，更对国家部门做出关于行业监管制度的警示提醒，无论从社会还是法律角度，都是一个不得不借鉴的范本。 ','邯郸农行5100万被盗案反思','1','ccc2 ',' ',' ','0','0','newspicture/U1596P1T1D13631469F21DT20070810042902.jpg');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('新华网洛杉矶8月9日电(记者 高原)美国加利福尼亚州南部9日凌晨发生震感强烈的地震，在当地引发恐慌。 　　<BR>据美国地质测量局称，这次里氏4.5级的地震发生在当地时间9日零点58分，震中在洛杉矶市东北部约50公里的切茨沃斯镇，洛杉矶市中心和附近地区也有明显震感。此后又发生了数次余震。 　　<BR>当地警方称，据初步调查，地震造成当地一座桥梁和多处房屋出现裂缝，被损坏的桥梁已被关闭.目前没有人员伤亡的报道。 　　<BR>据震区居民反映，他们是在睡梦中被惊醒的，当时房屋晃动，家中物品纷纷落在地上，数千辆汽车的报警器在震动后发出狂叫。 　　<BR>地震专家称，这是自1994年洛杉矶地区发生6.7级大地震以来，加州南部发生的最强烈地震。1994年的地震造成72人死亡，1万多人受伤，财产损失达125亿美元。 ','美国加州南部发生里氏4.5级地震','3','ccc3 ',' ',' ','0','0','newspicture/c5a1d4e594a00ff2eaaa15ce9b39d344.jpg');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('北京时间8月8日，最近又再度被热炒转会传闻的步行者球星小奥尼尔接受了官方网的采访，和之前一样的是，小奥尼尔依旧表示出了想继续留在步行者队的渴望以及对这支球队所抱有的希望，但他也指出假如球队想要打造青年军，那么他的离开对双方都是有好处的—— 　　问：你想在2007-08赛季为谁效力？ 　　<BR>小奥尼尔：我想呆在这里，我想继续当一位步行者球员，很显然，因为某些原因，我们已经有许多年打得不尽人意了，我们被许多事情分心，但我还是对这座城市充满了敬意。我与沃尔什进行了一番长谈，和伯德也有过，话题是球队的未来，但假如我们想走极为年轻的阵容路线，那么我呆在这里就没什么意思了，正如大家所知道的，这需要好几年的时间才能回到竞争行列中，除此之外，我也知道我的合同占去了很大的薪金空间。那么我自己认为步行者现在正在重建么？不，我们还没有做过任何惊天的交易，许多人也预计是否会将我交易出去来换回去他人，但到现在为止，我们还是做了一些非常适合球队的交易 ','小奥坚称想留守步行者 ','2','ccc4 ',' ',' ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('北京时间8月8日晚，北京奥运会倒计时一周年庆祝活动在天安门广场隆重举行。来自祖国各地和首都各界群众欢聚在伟大祖国的心脏，载歌载舞欢快的迎接2008年奥运会开幕倒计时一周年的重要时刻。来自两岸三地的众多演艺明星也加入到了表演的行列。 　　<BR>20点08分，第29届奥运会参赛邀请函发布仪式正式开始。现场由北京市长、北京奥组委副主席王岐山主持，首先讲话的是中国奥委会主席、国家体育总局局长刘鹏，他简单介绍了北京奥运会的筹备情况之后，刘鹏期待着奥委会的成员国在五环旗的照耀下相聚北京。 　　<BR>此后由北京市委书记、奥组委主席刘淇致辞，他首先回顾了申奥成功的激动一刻，刘淇说，申奥成功六年来，在首都各界，全国和奥委会的支持下，筹备工作进展顺利。场馆建设基本接近尾声，奥林匹克教育在全国进行，数十万志愿者正在北京开展微笑服务活动，传递着北京奥运“同一个世界，同一个梦想”口号。广大人民参与奥运蔚然成风，体现了奥林匹克精神，贯彻了北京奥运绿色奥运、科技奥运、人文奥运的三大理念，推动了经济的发展，环境的改善，城市更加文明和谐。未来一年中北京将集中精力做好奥运的筹备工作，不负重托为国争光。 　　<BR>随后国际奥委会主席致辞，他首先向北京人民和世界各国人民致意，并且向各奥委会发出邀请，请他们来北京参加2008年奥运会。罗格表示，北京奥运会的参赛奥委会之多，在数量上创了一个纪录，让自己非常欣慰。目前全世界都在以期待的目光注视着中国，运动员们也期待着在北京的竞赛，而其中有很多人将通过奥运测试赛有幸提前感受北京的场馆。 ','天安门广场见证伟大时刻 罗格现场发出奥运邀请函','1','ccc5 ',' ',' ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('2007-2008赛季英超联赛将在8月11日拉开大幕，四大豪门争霸曼联能否卫冕？谁能搅乱前四格局？三支升班马谁能带来惊喜？中国球员孙继海和董方卓又会有什么样的表现？新浪体育特邀ESPN英超专家詹俊先生为即将开幕的英超新赛季做一番评论。 　　<BR>我认为英超新赛季有五大看点，第一，曼联能否卫冕，四大豪门前景如何 　　<BR>这是最吸引人的环节，也是球迷们最关心的环节。曼联能否卫冕，切尔西前景如何？利物浦在引进强援后能否威胁到曼联切尔西，没有了亨利，阿森纳的前四位置会不会收到影响。 　　我个人认为，在新赛季里，至少是前半程里面，曼联的优势还是比较稳固的，他们上赛季夺冠最成功的地方可以或者说最大的收获，是老将继续发光，新人不断成熟并挑起大浪。弗格森拥有的是一支非常成熟的球队。今年夏天曼联又加强了中场力量，给球队添加了技术元素，我觉得他们的整体发挥会比上赛季更加稳定，去年时隔4年夺冠但整个过程惊涛骇浪，几场关键比赛赢得惊险。 　　<BR>弗格森重点培养的青训球员，通过上赛季磨练已经能够挑起大量，比如奥谢和布朗。奥谢是一个中后场中坚替补，无论是中前卫还是边后卫，处理球方面都非常成熟，没有浪费爵爷对他的栽培，上赛季两个关键的客场胜利，赢了利物浦的埃弗顿，他都有进球，证明他有一明显进步。布朗过往总觉得过于毛躁，但是经过磨练，无论是中卫还是做内维尔替身，都是称职人选。 ','英超新赛季五大看点','2','ccc5 ',' ',' ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('新浪体育讯　虽然丁俊晖没有再次倒在排名赛首轮出局的魔咒下，但是上海大师赛第二轮被多特1比5淘汰出局，这个结果显然不能让家乡观战的球迷感到满意，特别是丁俊晖在比赛中表现出来的心理状态更令喜爱他的球迷担心。 　　<BR>在丁俊晖以往的比赛中被人抨击最多的便是脆弱的心理素质以及抗压能力，从温布利大师赛输给火箭奥沙利文，在排名赛首轮出局貌似成为了丁俊晖挥之不去的梦魇，连续四次首轮出局像个巨大的阴影笼罩着丁俊晖，久久不能摆脱。进入新赛季，在训练中出色的状态，在欧亚对抗赛牛刀小试都让人们对丁俊晖新赛季的表现充满了期待，人们都渴望他能展现一个全新的面貌。 　　<BR>首轮击败实力相差较大的中国另一位选手于德陆，虽然取胜，但是丁俊晖却没有达到以往的颠峰状态，整场比赛除了第一局险些单杆破百能让人们依稀记得那个雷厉风行的神奇小子，除此之外，频繁的失误充斥着整个比赛，让人感到亮点不多，乏善可陈。反而在领先时急于求成的急躁再次显露了心理状态的不稳定。 ','丁俊晖最大瓶颈何时能突破','2','ccc6 ',' ',' ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('eNet硅谷动力消息据外国媒体报道，微软于周四表示正在寻求更多的人参加WindowsVista的测试工作。本月初，软件巨人称ReleaseCandidate1（RC1）版本已经公开提供，但消费者还没有进行这个新系统的测试。 ','RC1测试意见匮乏 微软呼吁的Vista测试者','4','','','eNet硅谷动力 ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('CNET科技资讯网9月15日国际报道 戴尔董事会主席迈克尔·戴尔否认了有关笔记本电脑电池失火与戴尔的PC生产方式有关的说法，他将全部责任推给了电池制造商索尼。','戴尔为笔记本召回辩解 问题电池责任推给索尼','4','','','CNET ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('新华网北京８月１１日专电　按照以色列工学院教授阿莫斯·奥里展示的一台时间机器的理论模型，人们也许真的可以像科幻小说中描写的一样进行时间旅行，探索过去和未来的世界。 ','以色列教授展示时间机器的理论模型','4','','','新华网 ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('中新网8月12日电 民进党2008参选人谢长廷今天在新加坡称，这段期间他已通过各种渠道邀请苏贞昌担任参选副手，希望苏贞昌答应，但到现在为止，苏贞昌没有答复，也没有说不要。','谢长廷称已邀请苏贞昌担任参选副手','1','','','中新网 ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('千龙网8月11日讯 是否还记得法国著名的小说家、科学幻想之父儒勒·凡尔纳写的《80天环游地球》，现在通过“银河套房”(Galactic Suite)，人们有望在2012年在80分钟内“轻松”环游地球一圈。 　　<BR>据路透社8月10日报道，在太空建造的第一个旅馆——“银河套房”的计划现在已经开始实施，预计在2012年对游客开放，届时三天的入住价格大约为400万美元，到时候游客可以在80分钟内环游地球一圈。 　　<BR>在24小时的时间里太空旅客可以看到15次日出，并且乘客可以穿着维可牢服装(Velcro suits)缓缓的进入他们狭小的卧室内并且可以像“蜘蛛侠”一样将自己“粘到”套房内的墙上体验穿檐走壁的“快感”。 　　<BR><BR>洗澡像泡SPA 　　<BR>据悉，在零重力的状态下，洗澡对于游客来说是最大的问题，但是建筑师们已经解决如何在失重条件淋浴的问题——游客将进入充满水泡漂浮的SPA房间内。当游客通过舷窗观看并不是很惊讶的景色时，他们可以在太空旅行中做各种太空上才能过做的实验。 <BR>　　<BR>梦想变为现实 　　<BR>据了解，“银河套房”已经成为是前航天工程师克拉拉芒特(Claramunt)的一种嗜好，而现在航天爱好者准备花30亿美元将科幻中的酒店变成现实。 　　<BR>据悉，一家美国公司的意图是移民火星，并将“银河套房”将作为其第一步计划，来自日本、美国和阿联酋的私人投资正在接洽之中。 　　<BR><BR>“富人不少” 　　<BR>据推算，世界上将有4万人能够支付得起‘银河套房’昂贵的费用，但是是否他们当中的每个人都愿意将钱花到‘空间’旅行上，目前还不得而知。 　　<BR>400万美元如果用在旅游度假上，将是一笔不小的数目，但是如果用在太空旅行上，这简直就是捉襟见肘，但是旅馆经营者相信，在短时间内太空旅行的费用不会降下来。 　　<BR>据悉，“银河套房”这么高的费用不仅包括在太空度过的3个“美妙”夜晚，并且游客在“上天”之前还能在热带海岛上享受到诸如在詹姆士·邦德(007)式的太空营内得到8周的高强度训练以适应日后来到太空的环境，而这些绝对是超值的服务。 　　<BR>克拉拉芒特说：“只要提到太空旅行，人们就自然想到危险，这就是为什么在游客居住期间太空梭需要不断的飞来维护太空旅馆，只有这样游客们才能知道他们能安全的返回地球”。 　　在世界气候不断变化的当今，克拉拉芒特说：“我希望游客通过在太空看到人类自己居住的星球能刺激他们更加爱好自己的地球”。(石磊) ','太空旅馆计划开始实施 入住3天需400万美元','2',' ',' ','千龙新闻网 ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('新浪科技讯 北京时间8月12日消息，据国外媒体报道，美国犹他州地方法院法官戴利·基姆巴尔(Dale A. Kimball)周五裁定，Unix操作系统的版权归属于Novell，而不是SCO。分析人士认为，这一裁决有望结束SCO同IBM围绕Unix版权展开的旷日持久的法律纠纷。 　　<BR>在长达102页的裁决书中，基姆巴尔还表示，Novell可以迫使SCO放弃针对IBM的指控。由此看来，这一裁决几乎肯定会影响到SCO同IBM之间的侵权诉讼案。与此同时，这一裁决还将扫除长期以来笼罩在开放源代码操作系统Linux头上的阴云，因为从广义的角度来看，它也是Unix的一个派生产品。长期以来，由于Unix版权归属问题一直悬而未决，很多企业客户因为担心遭遇法律纠纷，而在是否采用Linux的问题上犹豫不决。 　　<BR>哥伦比亚大学法律教授埃本·莫格伦(Eben Moglen)表示：“由于Linux可能涉及版权纠纷，很多人认为开放源代码软件存在风险。但事实最终证明，开放源代码世界从创建之日起就非常稳固。上周五，SCO股价在纳斯达克报收于1.56美元，远远低于2003年的最高点19.41美元。 　　2003年3月，SCO在美国提起诉讼，指控IBM将该公司拥有专利的部分Unix组件用于Linux操作系统，这种行为侵犯了知识产权。SCO表示，该公司于1995年从Novell购买了Unix版权，而Linux是一个未经授权的Unix派生产品。在此之后，Novell发表公开声明称，Unix版权仍然归该公司所有。 　　<BR>Novell高级副总裁兼首席法律顾问乔·拉萨拉(Joe LaSala)周五表示：“法官的最终裁决使得SCO对IBM的起诉失去了基础，同时也消除了对Linux社区的威胁。”到目前为止，SCO还没有就此发表评论。Unix最初由AT&T贝尔实验室研究人员开发，上世纪70年代在学术计算和科研部门发挥了重要的影响力。上世纪80年代，Unix开始广泛应用于工作站和小型计算机，但没能成功进入PC市场。(马丁） ','美法院裁定Unix版权归属 扫除Linux头顶阴云','3',' ',' ','http://www.sina.com.cn ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('新浪体育讯　北京时间8月12日消息，热火队在让人惊讶的签下了已经一年没有参加职业联赛的安芬尼-哈达威之后还在意图加强球队的整体实力，毕竟今年夏天东部的几支中下游球队都通过转会让实力得到了暴增。 　　<BR>上赛季效力金州勇士的摇摆人皮特鲁斯一直是热火队的心仪人选，这位已经在联盟效力了四个赛季的法国球员本周在接受法国当地媒体的采访时公开向热火队献媚，“热火是是一支伟大的球队，要是能和韦德以及奥尼尔一起并肩作战，我将非常乐意。” 　　<BR>但是目前的情况是，热火队将面临其他球队的竞争，其中他的东家勇士队有意和他续约。皮特鲁斯的经纪人比尔-迈克坎德莱斯表示他在本周已经和热火队的总经理兰迪-布冯德进行了接触，“兰迪一直都非常积极，”迈克坎德莱斯评价说，不过由于热火已经用中产条约中的一部分合同金额去签下了上赛季效力于湖人队的先发组织后卫斯马什-帕克，因为球队没有足够的薪金来直接满足这位自由球员，不过先签后换仍然可行。 　　<BR>如果能够成功引进皮特鲁斯，那么这对于今夏在转会市场上缺乏动作的热火队而言将有着极为重大的意义。韦德在评价球队最近的转会动作的时说，“看沙克和哈达威再次穿上同一支球队的队服是很有去的事情，在‘便士’职业生涯的早期的时候，他可是所有人都期待能够得到的球员，他是那种能够让我拿出我的笔记本作出一些标注的家伙。” 　　<BR>引进皮特鲁斯的谈判还在继续，而热火队更为实际的动作是签下了24岁的后卫德文-格林。这位汉普顿大学毕业的年轻球员身高2米01，体重只在95公斤，此前只在2005-06赛季在湖人队短暂的打了27场比赛，而过去的一年他在NBDL以及德国混迹。签下这位年轻而且不知名的球员，热火队既是为了满足后卫缺人的需要同样也是因为薪资严重受限的原因。(大脸猫） ','勇士摇摆人期待联手奥尼尔韦德 热火阵容再添新人','2',' ',' ','新浪体育 ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('中华老字号首次海外诉讼维权 　　<BR>晨报讯(记者 孙昶)被海内外广泛关注的我国百年老字号“王致和”商标被德国OKAI(下简称欧凯)公司抢注案终于开庭了，这是中华老字号首次进行海外诉讼维权。王致和集团起诉德国欧凯公司商标侵权和不正当竞争案，在慕尼黑当地时间8月8日上午11点(北京时间8月8日下午5时)在慕尼黑地方法院正式开庭。 　　<BR>在庭审当中，双方进行了激烈的交锋，争议的焦点主要集中在： 1、“王致和”主张对方是恶意抢注，对方强调其是在德国合法注册；2、“王致和”主张对王致和商标有著作权，而对方则称其标识是通用的“中国古代士兵头像”；3、“王致和”提出，对方违反德国的反不正当竞争法，对方则辨称其注册王致和的商标是对自己的保护。 　　<BR>据“王致和”的代理律师王洪青介绍，被告在9月15日之前还有一次补充证据的机会，如果双在11月14日前达不成和解，法院将进行判决。王洪青律师还告诉记者：“‘王致和’的产品要去德国销售，如果商标不夺回来，对方可能会主张权利，我们就有可能构成侵权。” 　　据介绍，王致和集团的律师团队在收集证据的过程中发现，欧凯公司不仅抢注了王致和的商标，而且在更早的时候还抢注了老干妈、洽洽、今麦郎、白家、郫县豆瓣酱等其他国内知名食品的商标。王致和集团目前在全球34个国家和地区抓紧注册，以避免商标再次在国外被抢注。 ','王致和商标遭抢注案德国开庭 老字号海外维权','1',' ',' ','http://www.sina.com.cn ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('<P>本报韩国首尔8月10日电(记者谢湘)在韩国举行的微软“创新杯”2007(Im agine C up 2007)全球学生大赛决赛结果今天正式公布，在9个项目所设的27个奖项中，中国大学生获得4枚奖牌。 　　<BR>微软“创新杯”全球学生大赛始创于2003年，旨在鼓励年轻人发挥想象力、热情和创造力，投身科技创新，为当今世界作出贡献，已成为世界上规模最大的学生科技竞赛活动。今年大赛吸引了来自全球100多个国家和地区近10万学生报名参加，经过激烈角逐，59个国家和地区的112支代表队进入总决赛。 　　<BR>中国150多所高校的7000多名学生参加了比赛，华中科技大学联创团队在软件设计项目的中国区决赛中获得第一名，代表中国参加该项目的总决赛，6所高校的9支团队在不分国籍的在线项目中表现优异，赢得了全球总决赛入场券。 　　<BR>在决赛中，来自武汉华中科技大学的大二学生陈志峰获得IT项目比赛第一名，北京工业大学Frontfree Studio -UI 和 Frontfree Studio -W eb 两支团队分别获得界面设计、网络开发项目的第二名和第三名、北京邮电大学的SEED团队获得嵌入式开发项目第三名。 　　<BR>今年大赛的主题是“科技可以使所有人受到更好的教育”，共设置了软件设计、嵌入式开发、网络开发、逻辑算法、IT挑战、视频游戏、摄影、短片制作和界面设计等9个项目。 　　<BR>微软公司大中华区开发合作部总经理柏尚杰说，中国队是本次大赛获奖最多的代表队，在这个全球性的IT平台上，中国学生展示了他们世界级的技能以及在未来产业中超越其他国家的实力。 </P>','十万学生角逐微软创新杯 中国大学生获奖最多','1',' ',' ','http://www.sina.com.cn ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('上周五，微软中国在钓鱼台举行新闻发布会，与中国信息安全产品评测认证中心（以下简称评测认证中心）签署了新一期的政府安全计划源代码协议。　　<BR>按照某国内媒体的报道，根据协议，中国信息安全产品测评认证中心和相关被授权机构，将被授权在线即时查看包括Windows Vista、XP、2003、2000和CE6.0、5.0及4.2版的专业版全部源代码，以及office专业版、2003及office系统中的任何附加产品在内的绝大部分现有版本的全部源代码及相关技术信息。　　<BR>有媒体认为，微软此举将使“Vista后门说不攻自破”。但是，笔者向来不惮以最坏的恶意来推测国内媒体。难道微软真的会“公开”Vista全部源代码吗？　　<BR>话说这个“政府安全计划源代码协议”乃是关于加强软件产业合作备忘录（二期）的重要组成部分。也是政府安全计划（以下简称GSP）协源代码议的重要组成部分。而GSP是微软面向政府层面，授权签约政府以可控的方式查看微软软件产品源代码的一项政府合作计划。中国于2003年第一次与微软签署此协议，是全球首批参与这一计划的国家之一。　　<BR>但是，正如倪光南所说：“尽管微软许诺向中国政府100％地公开其全部源代码，但根据微软在世界其他国家实施GSP计划的情况来看，都没有做到100％。可以断定，中国不可能得到比他们更优惠的待遇。”评测认证中心在2003年就确认过这一点。　　<BR>“如果不是100％的开放，源代码是不完整的，根本就不能就信息安全性做出任何结论。因为只需区区的几行源代码，就足以在一个软件中引入‘后门’”，倪光南如是说。这样的话，又怎么能说“Vista后门说不攻自破”了呢？　　<BR>而关于GSP计划的报道中的“疏漏”并不止与此。事实上，GSP的限制非常之多，只不过被部分媒体有意无意的略过了…… 　　<BR>平心而论，GSP的开放程度远不能和开源运动简直是风马牛不相及。首先，微软的源代码是商业产品，其开放对象是政府，而Linux中的源代码则是自由软件，其开放对象是公众；其次，对源代码的处理不同，对于自由软件而言，用户有使用、修改和复制的自由，而微软的源代码只能以在线方式查看，部分涉及到第三方知识产权的源代码，微软无权与签约方分享。　　<BR>事实上，关于微软对华合作活动的误读又何止于此呢？　　<BR>微软与中国发改委签署的关于继续加强软件产业合作（二期）的谅解备忘录，未来5年涉及金额超过37亿美元。结果就被媒体解读为微软未来5年对华投资37亿美元。但实际上呢？37亿中有35亿是硬件采购——微软即使不和发改委签约，也可能要到中国来采购；剩下的2亿，又有1亿是软件外包；换言之，37亿元中只有1亿才能算是实打实的投资！　　<BR>而在这1亿元的投资中，微软也是收益不菲。比如微软曾经以区区2500万美元的代价，就获得了浪潮国际超过四分之一的股份，并间接获得其它优质企业的股权。其.net平台也凭借与浪潮国际的合作关系，曲线进入了外资很难直接进入的电子政务平台。微软与中国的合作中，谁受益更多，不言自明…… 　　<BR>也许正是不错的收益，微软近期也加快了与中国企业的合作。在7月31日签约亿阳信通之后，微软在过去的5个月内，与5家中国企业结成战略或技术合作关系，超过了过去5年的总和。目前，微软的合作伙伴已遍及政府、电信、金融、服务外包、数字娱乐等各个行业。微软也借此不断推进在中国的产业布局，并已曲线争取到了不少行业用户的订单。　　<BR>综上所述，我们不难发现微软在对华合作中纯粹只是一只利益动物而已。实际上，这也无可厚非。因为微软本来就是一家以赢利为第一目标的企业。无论是GSP还是此前的Vista价格雪崩，都不过是微软对中国市场公关的一种手腕而已，完全不用大惊小怪。　　<BR>只是部分媒体在报道微软中那些有意无意的“省略”，着实值得让人玩味…… 文章引用自： ','微软会公开Vista全部代码吗？','4',' ',' ',' ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('Google胜过百度 最近一咨询公司做了中国2007年搜索质量盲测评估报告，在盲测参与者完成的全部11,864次测试中，判断Google搜索结果好于百度的比例48.2%，百度搜索结果好于<BR>Google的比例为39.8%，Google的中文搜索质量在八个日常搜索分类领域中的七个超越百度，百度仅在娱乐搜索分类领域中胜出。从报告可以看出Google搜索质量是明显优于百度的，但为什么Google仍然败给了百度呢?这部分原因是跟Google的市场推广、本地化不够有关，但从报告还可以得出Google败给百度的真正原因。 Google败给百度 从评估报告中有一项很重要的指标“对百度和Google提供的搜索服务不满意的方面”，从这里可以看出对百度不满意的是对搜索结果的质量、客观性、范围和相关性，这几乎是对搜索引擎核心功能的全面否定。 而对Google不满意的却是它的附加服务，例如快照不能使用，这是Google在中国妥协的结果，而不是它自身不能提供这项服务；搜索不稳定我倒很少遇到过，但是有时出现页面不能访问的情况，大多跟国内网络的监控有关，这也是败在国内特殊环境之下；未提供MP3搜索，是因为MP3涉及版权纠纷，不做恶的Google也只能无言以对；不能提供百度贴吧算是Google中国本地化不够的一个方面，但跟搜索引擎本身并不相关。所以Google败给百度不是败在搜索上，而百度胜过Google却主要靠着外部环境的因素。如果哪一天Google的这几项备受困扰的服务和功能可以开放、推出，那百度在市场上败给Google是迟早的事情。 ','Google胜过百度还是Google败给百度','4',' ',' ',' ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('<P>“电脑与他的灵魂之间似乎有一条脐带相连.这就是为什么只要在计算机面前,他就会成为巨人的原因.”———美国联邦调查局某特工 凯文\"米特尼克,1964年生于美国加州的洛杉矶.13岁时他对电脑着了迷,掌握了丰富的计算机知识和高超的操作技能,但却因为用学校的计算机闯入了其他学校的网络而被勒令离校. 15岁时,米特尼克成功入侵了“北美空中防务指挥系统”的主机,成为黑客史上的一次经典之作. 不久,他又成功破译美国“太平洋电话公司”在南加利福尼亚洲通讯网络的“改户密码”.随后,他又进入了美国联邦调查局(FBI)的电脑网络,吃惊地发现FBI的特工们正在调查的一名电脑黑客竟然是他自己!他立即施展浑身解数,破译了联邦调查局的“中央电脑系统”密码,每天认真查阅“案情进展情况报告”,并恶作剧地将特工们的资料改成十足的罪犯.不过,特工人员最终还是将米特尼克捕获,法院将他送进了少年犯管教所,米特尼克成为世界上第一个“电脑网络少年犯”. 很快,米特尼克就获得假释.1983年,他因非法通过ARPA网进入五角大楼的电脑网络而被判在青年管教所6个月.1988年因为入侵数字设备公司DEC再度被捕.1990年,他连续进入了世界5家大公司美国太阳微系统公司、Novell网络公司、NEC公司、诺基亚公司和摩托罗拉公司的网络,修改计算机中的用户资料,然后逃之夭夭.1994年12月25日,米特尼克攻击了美国圣迭戈超级计算机中心,因此获得“地狱黑客”的称号.但是,这次攻击激怒了负责该中心计算机数据安全的著名日籍专家下村勉,为挽回损失和教训米特尼克,这位计算机高手利用自己精湛的安全技术,帮助FBI将米特尼克捉拿归案. 联邦法院以25宗非法窃取电话密码、盗用他人信用证号码和闯入他人网络的罪名起诉米特尼克,而且未经审判就将米特尼克关押了4年半,并且不得保释,这是美国司法史上对一名高智商罪犯所采取的最严厉的措施. 2001年1月,米特尼克在认罪后,获得了监视性释放. “黑客们变得越来越老练和狡猾,他们会想出各种新的花招,利用技术漏洞和人性的弱点来劫持你的计算机系统.”———凯文\"米特尼克 获得自由后的米特尼克,目前投身于计算机安全咨询和写作中.他穿梭于世界各地,告诉人们在一个充满工业间谍和众多比他更年轻的黑客世界里,如何保证自己的信息安全. </P> <P>他在一次转机的间隙,写下了以下十条经验与大家分享.<BR> ●备份资料.记住你的系统永远不会是无懈可击的,灾难性的数据损失会发生在你身上———只需一条虫子或一只木马就已足够. <BR>●选择很难猜的密码.不要没有脑子地填上几个与你有关的数字,在任何情况下,都要及时修改默认密码. <BR>●安装防毒软件,并让它每天更新升级.<BR> ●及时更新操作系统,时刻留意软件制造商发布的各种补丁,并及时安装应用. <BR>●在IE或其它浏览器中会出现一些黑客鱼饵,对此要保持清醒,拒绝点击,同时将电子邮件客户端的自动脚本功能关闭. <BR>●在发送敏感邮件时使用加密软件,也可用加密软件保护你的硬盘上的数据. <BR>●安装一个或几个反间谍程序,并且要经常运行检查. <BR>●使用个人防火墙并正确设置它,阻止其它计算机、网络和网址与你的计算机建立连接,指定哪些程序可以自动连接到网络. <BR>●关闭所有你不使用的系统服务,特别是那些可以让别人远程控制你的计算机的服务,如RemoteDesktop、RealVNC和NetBIOS等. <BR>●保证无线连接的安全.在家里,可以使用无线保护接入WPA和至少20个字符的密码.正确设置你的笔记本电脑,不要加入任何网络,除非它使用WPA.要想在一个充满敌意的因特网世界里保护自己,的确是一件不容易的事.你要时刻想着,在地球另一端的某个角落里,一个或一些毫无道德的人正在刺探你的系统漏洞,并利用它们窃取你最敏感的秘密.希望你不会成为这些网络入侵者的下一个牺牲品. </P>','世界第一黑客提出计算机安全十大建议 ','4',' ',' ','《科技日报》 ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('最近，我被著名评论人刘兴亮先生忽悠到北京，开始了自己的北漂岁月，北漂的感觉终于体验到了，阿门！ 北京IT氛围还是很好的，到处涌动着创业的冲动和财富梦想，林林总总的小企业遍地都是，所以很多人有感触，“到了北京，看到这么多公司，都晕了”。不过，公司虽多，找个适合自己的还是真难啊，我转悠了大半天，依然是北漂待业青年。 创业型的公司一般都人数有限，工资不高，完全不是外界所想的it职业美景，不过都比较累，因为什么不干坐在电脑前就很累.北京IT小企业离职率高的原因很多，偶列举一二：<BR> 1. 只加班不给钱。不少公司是这样的，公司说没办法，员工说没意思。 <BR>2. 老板实行永远试用期制度。在试用期没完的时候，把员工都压迫走人了。 <BR>3. 管理者能力和修为问题。并不是当了老板或CEO，就具备了管理和领导企业的能力，权利的控制者过分追求自我意志，但是新公司无法保证员工的安全感。 <BR>4. 员工培训问题。员工创造价值，必须培养员工，新公司在培养员工上要更加耐心 <BR>5. 资金链。很多企业的资金链无法保证，这个对于互联网企业更加严重，概念与盈利差距太远。 能进新浪、搜狐这样的企业毕竟是少数，虽然他们也不轻快，创业不容易，it打工者更不容易啊，加上北京房价吓人，生活开销愣大，日子不会好过到那里。 整天在忙碌，却似乎没得到什么，it人的幸福和其他行业一样，被通货膨胀给剥削掉了。GDP 啊，GDP，饶我们一条命吧 ','北京IT小企业离职率高的原因 ','1',' ',' ',' ','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('近日的三条重头新闻让笔者思考了许久。第一条是电子百强的发布；第二条是世界品牌500强联想排名暴跌；第三条是联想与浪潮服务器一季度跌出全球前十。 首先是电子百强的排名，亏损企业首先榜上有名令人费解。难道是我们的百强排名如果不把亏损企业也算在内的话，连百强的数目都凑不够吗？抑或是如果不把这些亏损的企业算在内而用另外企业代替的话，不但是亏损的，就连销售额也会很难看吗？其次，连排名靠前的联想、华为、中兴等企业的利润率也呈下降趋势。总之，亏损企业的上榜、利润率的下降无论从哪个角度讲，都无疑说明我们企业的竞争力正在下降。而且从与创新密切相关的研发投入上，我们的百强仍旧没有达到国际公认的比例，如果企业没有创新，那么何谈竞争力的提高。值得一提的是，就在我们的百强公布之前不久，美国的《商业周刊》发布了50强排名和全球最具创新力25家公司排名，三个排名的对比，无论从企业收入、盈利和创新能力上，无不令我们的百强相形见绌。实际上不仅仅是我们的企业，就连我们百强的排名标准和人家也有很大的差距。 再看世界品牌500强排名，联想排名暴跌。从排名上看，联想的排名从去年的110名，跌至今年的337名，排名竟然下降了227名。之所以重点提及联想的排名，是因为联想是当今中国IT企业国际化的代表并可能是唯一成功的希望。本以为收购IBM PC的联想的品牌影响力应该有所提高，加之联想所谓的体育营销，应该是更上层楼，但没料到的是品牌的大幅缩水。这至少从某种角度说明，联想在并购IBM PC后的两年内，品牌的建设和推广并未达到预期的效果，甚至有可能原IBM PC的品牌也因为联想的收购而缩水。这个结果也同时证明了前不久国外相关公司和人士对于联想不是品牌和品牌命名欠妥的评论不是毫无根据和道理。 第三就是我们始终认为是自主创新最强领域的服务器的排名被挤出前十。尽管联想、浪潮服务器在国内的市场仍在增长，但从全球的角度看，他们的竞争力正在下降。其实这样的结果并不惊奇。首先，服务器对于联想来说，只是边缘性的业务，谁都知道联想的重心是在PC上。再看一直蝉联国内服务器市场首位的浪潮，今年年初的内部管理的变革（服务器部门划归浪潮集团直接管辖）之后，其市场化的运作态势骤减，国企管理的弊端初现，截至到目前为止，浪潮基本上没有什么像样的技术和产品的发布，策略上也没有清晰的思路展现于业内。而与浪潮并行的曙光除了年初有过一次所谓的战略性发布之外，更多地是在自身市场份额极小的情况下（例如刀片服务器，IBM和HP占领了近90%的市场份额，曙光的份额简直可以忽略不计），空洞地呼吁建立中国自己的高性能计算标准（以刀片服务器的标准切入），而实质性的技术和产品的发布与浪潮一样匮乏，浮躁和急于求成的心态溢于言表。在国外服务器厂商不断打压下，国产服务器厂商往昔的辉煌恐怕难以再现。 其实除了上述三条新闻外，我们所谓的骨干企业都面临艰难的处境。2006年，电信设备制造商中兴的收入减少40%，这其实也从另一个侧面映射出其竞争对手华为也面临着类似的困境。而曾经被认为可以超越中国台湾半导体制造对手的中芯国际和上海宏力也都面临着困境。例如中芯国际自2004年首次公募以来，其在芯片制造业中所占份额已经下跌了近54%，在其第一季度财报时，尽管其自己宣称盈利900万美元，但有国外机构称，如果没有之前的财务盈余，中芯国际可能会亏损4000万美元。在手机市场，诺基亚、摩托罗拉和三星在中国市场的强力竞争，使得国内的TCL和波导等在内的手机厂商的市场份额出现缩水。 我们企业的核心竞争力在哪里？我们真的理解它的含义吗？政府支持真的是企业做大做强的保证吗？我们企业所谓的创新究竟是不是创新？我们的心态是否依旧浮躁和务虚？我们的企业真的应该冷静下来好好梳理这些问题了！ ','中国IT企业的竞争力下降了吗？','4','','','','0','0','');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('<P>新浪体育讯　北京时间8月17日，美国NBA专家伊恩-汤姆森发表文章，评出了NBA史上全明星队，根据位置选了12名最有影响力的球员以及一名教练。来看看他心目中的最强12人都是谁吧。 　　<BR>从比尔-拉塞尔到科比-布莱恩特，NBA联盟经历了很多变化，单纯从球员实力上评出最强阵容实在难保公平，因此我们应该更多的考虑球员在所处年代是否具备统治优势，要考虑他们赢得总冠军的次数。同时季后赛的表现非常重要，能统治季后赛的才是最好的球员，比如奥尼尔。但威尔特-张伯伦则没有做到，当时他只能排在拉塞尔之后。因此鲨鱼能够入选这支球队而“篮球皇帝”却不行。 　　<BR>这12名球员都有总冠军戒指，其中10人赢得过MVP，例外的两人是约翰-海弗里切克和杰里-韦斯特，这二人没能赢得MVP绝不是因为实力不够。而最难的选择在于科比，很多人认为他应该名列其中，我在一开始也是这样选择的，但最终的选择是鲍勃-库西，这位凯尔特人名宿连续10个赛季入选最佳阵容第一队。 　　<BR>以下为史上全明星对首发阵容： 　　<BR><BR>小前锋：拉里-伯德 凯尔特人(1979-1992年) 　　<BR>在前半段职业生涯，伯德的运动能力其实很出色。“大鸟”在得分和助攻方面表现得像一个超人，他可以在球场的任何位置命中篮筐或者送出妙传。同时伯德还是公认的历史上最好的关键先生，史上唯一一位赢得常规赛MVP奖项的小前锋球员。 　　<BR><BR>大前锋：蒂姆-邓肯 马刺(1997年至今) 　　<BR>作为史上最好的大前锋，邓肯就是当今的比尔-拉塞尔。在邓肯的年代，大前锋是联盟中最强的位置，而石佛则是其中的佼佼者，他在球场两端都表现出了统治能力。 　　<BR><BR>中锋：比尔-拉塞尔 凯尔特人(1956-1969年) 　　<BR>拉塞尔在13年里拿下11次总冠军，其中包括一次8连冠。他把盖帽带进了这个联盟，也开创了总冠军头衔更重过得分王的历史。拉塞尔无疑是篮球史上最有影响力最有地位的球员。 　　</P> <P>组织后卫：埃文-约翰逊 湖人(1979-1991年；1995-96年) 　　<BR>作为控卫，“魔术师”有不同寻常的身材，出众的运动能力和领袖气质，他带来湖人在12年中9次打入总决赛5次夺冠，这一纪录仅次于拉塞尔。 　　</P> <P>得分后卫：迈克尔-乔丹 公牛(1984-1993；1994-1998)，奇才(2001-2003) 　　<BR>最辉煌的6个赛季里，乔丹同时赢下得分王和总冠军，除了他能做到这一点的只有乔治-麦肯(两次，1949-50赛季和51-52赛季)，贾巴尔(70-71赛季)和奥尼尔(99-00赛季)。 　　</P> <P>主教练：奥尔巴赫 　　</P> <P>替补阵容： 　　<BR>中锋：贾巴尔，奥尼尔；前锋：鲍勃-佩蒂特，约翰-海弗里切克；后卫：奥斯卡-罗伯逊，杰里-韦斯特，鲍勃-库西。 　　<BR></P> <P>(小柯) </P>','NBA史上最强阵容:邓肯最佳大前 奥尼尔挤掉张伯伦','2','myt ','小柯 ','http://sports.sina.com.cn ','0','0','newspicture/U2138P6T12D3108676F44DT20070817030528.jpg');
insert into newsArticles(content,title,typeid,username,writer,source,hints,checkup,imagepath)values('中国科学院研究生院教授吕本富博士在微软亚洲研究院科技与人文演讲系列中，以《网络经济发展的路径》为题，生动描述了目前流行的长尾理论以及典型的长尾现象。吕教授是中国培养出来的第一个网络经济学博士，同时也是著名的IT评论家。下面，让我们一起来分享一下吕本富教授在演讲中的部分内容。 一．IT产业的转型 在2000年的网络经济高潮中，起主导作用的是厂商。大公司的IT投资为网络经济贡献了现金流和利润，由信息化带动了网络经济的兴起和兴盛。然而，现在的世界各大厂商在信息化方面的投资逐渐减少。这主要是因为它们的信息基础建设基本完成，不可能再持续投资下去了。取而代之的是，硬件和软件产业逐渐让位给以网络为核心的商业形态，以厂商为主导的网络经济已经转变成以消费者为主导。这其中，就产生了两大金矿：一个是网络信息资源“金矿”；另一个则是网络人际关系圈“金矿”。在新转型后的网络经济中，如果一个公司想继续成为霸主，那么必须符合两个条件：第一，一定要符合网络经济本身发展的规律。第二，一定要跟草根相联系。比如说IBM, 它现在基本不跟草根联系了，甚至把它的笔记本也卖了，就只能回缩到和Oracle一样去细分市场了。所以，从目前的发展格局而言，能在IT业和网络经济同时进入主流的，微软是其中毋庸置疑的一家公司。 从用户的角度而言，消费者也逐渐从网民转向网商。从统计数据表明，中国目前拥有1亿以上的网民，5千万左右的网友，以及大概1千万的网商。简单地说，网民是贡献眼球的，网友是贡献内容的，而网商则是获得价值的。从网民到网商，网络经济给许多人带来了实际的价值，于是就摆脱了泡沫经济的帽子。因为价值的产生，就意味着泡沫的不复存在。 从聚合角度而言，Web1.0已经过渡到了Web2.0的时代。技术创新主要围绕着“资源聚合”进行。Web1.0主要实现了信息的聚合，使互联网成为了一个巨大的媒体。而Web2.0则实现了网友之间的互动，互联网成为了一个原创信息的平台，它主要通过博客 (Blog), 交友 (SNS), 定制 (RSS), 知识社区 (Wiki) 等形式组合而成。从Web1.0到Web2.0，网络经济的商业模式发生了本质的变化，Web2.0已经成为网络经济创新的源泉。草根文化变成了互联网的主流，为新一代的创业者提供了发展平台。 二．长尾理论及其关照 美国《Wired》杂志主编Chris Anderson提出了时下流行的长尾理论（Long Tail），与互联网相关的一系列现象好像如法炮制都能扯上一些长尾的说法。大家看上面的图示，红色部分表示处于20%的传统大客户，我们将它比喻成西瓜；竞争激烈，成为红海；蓝色部分是处于80%的中小客户；我们将它比喻成芝麻；聚沙成塔，成为蓝海。互联网就是将蓝色部分变成自己的长尾，聚沙成塔，完成了传统经济下无法聚集的功能。那么这其中的关键就在于，如何降低捡芝麻的交易成本，以网络平台和网络的聚合效应提供成功的可能。举例来说，美国有两个网站，一个是Rhapsody这个网站，另一个是MP3网站。MP3坚持的是纯草根路线，不跟任何一个唱片公司签约，只顾尾巴，所以不久就倒闭了。而Rhapsody网站签了20％的像周杰伦这样的大牌明星，剩下的80％是草根，反而取得了成功。由此我们可以看出，在网络经济条件下，头和尾，并不是对立的，完全对立就会失败。同时，蓝的部分确实是越来越重要，红和蓝，就如同西瓜和芝麻的关系。在传统条件下，大家都抢红的西瓜，你捡我也捡，把西瓜捡成了红海。反而，那80%的芝麻过去是没人要的，甚至有人认为它不存在。其实把芝麻加起来的价值也很大，问题就在捡一粒芝麻的交易成本过高。互联网带给我们的好处就是降低捡芝麻的成本，把每一次弯腰捡芝麻的成本进行削减。 我们一起来看看Google是如何成为长尾的。 Google过去20%的大广告客户，面临着门户网站、电视台等媒体打家的竞相争抢，广告商之间的竞争愈演愈烈。后来，Google提出了通过自动填表成为广告客户的营销模式，几乎不存在什么交易成本，于是捡芝麻的成本降低，Google聚沙成塔，变成了一个巨无霸。这也说明了网络经济由厂商时代变成消费者时代的道理。 不仅仅是Google,还有很多公司的成功都符合长尾理论。那么究竟什么情况下能够形成长尾呢?吕本富教授总结出了三条法则：第一，应有尽有（Make Everything Available）。只要是在Web2.0的平台之下，消费者需要的信息，都能找到，这是第一个基本原则。比如亚马逊网站提供的几万种图书目录，就是一个很好的资源或信息聚合的例子。第二，关联推荐(Help Me Find It)。畅销的头部带动非畅销的尾部。比如说，易中天教授现在很火，于是连他十年前未成名的书也火了起来。这就是用畅销的部分带动非畅销的部分，形成了需求聚合，把细小的需求和流行的需求聚合放在一起。第三，半价服务，或者称降低摩擦(Cut the Price in Half, then Lower It)。通过互联网把本来稀缺而昂贵的东西实现廉价，通过减少物流与库存环节而降低成本。以上所述的三条原则，资源聚合、需求聚合、降低成本，基本描述了长尾理论的基本概?','网络经济，谁主沉浮？','4','myt ',' ',' ','0','0','newspicture/4caedc7a02001440.jpg');

#评论表
create table reviews
(
	id				int				auto_increment				primary key,
	articleId		int,
	foreign key (articleId) references newsArticles(articleId),
	userName		varchar(100)	not null,
	body			varchar(1000)	not null,
	face			varchar(20)		not null,
	dateandtime		timestamp		default current_timestamp
);





